    Dim headerStyle2 As New StyleInfo()
    headerStyle2.Background = Brushes.DarkSlateGray
    headerStyle2.Foreground = Brushes.White
    gcSpreadGrid1.NamedStyleInfos("Header1") = headerStyle2
    Dim cellStyle As New StyleInfo()
