    columnStyle.Background = Brushes.LightGreen
    columnStyle.Foreground = Brushes.Black
    Dim headerStyle As New StyleInfo()
    headerStyle.Background = Brushes.DarkOliveGreen
    headerStyle.Foreground = Brushes.White
