    textCellType1.Multiline = true;
    textCellType1.AcceptsReturn = true;
    textCellType1.WatermarkDisplayNull = "DisplayNullText";
    textCellType1.WatermarkDisplayNullForeground = new SolidColorBrush(Colors.Gray);
